[aria-label='Friend Activity'] {
  transition: width 0.3s;
}
@media screen and (max-width: 1200px) {
  [aria-label='Friend Activity'] {
    width: 0;
  }
  [aria-label='Friend Activity'] .LayoutResizer__resize-bar {
    display: none;
  }
}

.main-connectBar-connectBar {
  overflow: visible !important;
  --triangle-position: 147px !important;
  align-items: unset !important;
  height: 0px !important;
  position: absolute !important;
  left: 80% !important;
  display: flex !important;
  bottom: 2% !important;
  padding: unset !important;
}

.main-rootlist-expandArrow {
  -webkit-transform: rotate(-90deg) !important;
  transform: rotate(-90deg) !important;
}
.main-rootlist-expandArrow:hover {
  -webkit-transform: rotate(-90deg) !important;
  transform: rotate(-90deg) !important;
}
.qAAhQw9dXNB7DbPgDDxy {
  -webkit-transform: rotate(0deg) !important;
  transform: rotate(0deg) !important;
}
.qAAhQw9dXNB7DbPgDDxy:hover {
  -webkit-transform: rotate(0deg) !important;
  transform: rotate(0deg) !important;
}

.Root__nav-bar {
  position: absolute;
  width: 40px;
  opacity: 0;
  bottom: 0;
  left: 0;
  top: 0;
  z-index: 1;
}
nav.Root__nav-bar:hover {
  position: inherit;
  width: 225px;
  opacity: 1;
  transition: visibility 5s, opacity 0.5s linear;
}
.LayoutResizer__resize-bar {
  cursor: none;
}
.Root__top-bar {
  opacity: 0;
  transition: visibility 5s, opacity 1s linear;
}
.Root__top-bar:hover {
  transition-delay: 0.5s;
  opacity: 1;
  transition: visibility 5s, opacity 0.5s linear;
}
.main-topBar-container {
  -webkit-padding-end: 32px;
  padding: 16px 85px;
  padding-inline-end: 32px;
  max-width: none;
}
aside.main-buddyFeed-buddyFeedRoot:hover {
  position: inherit;
  width: var(--buddy-feed-width);
  opacity: 1;
  transition: visibility 5s, opacity 0.5s linear;
  left: 0;
}
aside.main-buddyFeed-buddyFeedRoot {
  position: absolute;
  width: 65px;
  opacity: 0;
  bottom: 0;
  left: -30px;
  top: 0;
  z-index: 1;
}
